import React from 'react'
import {useContext} from 'react'
import AlertContext from '../../context/Alert/AlertContext'
function Alert() {
  const { alert } = useContext(AlertContext);

  const alertStyle = {
    backgroundColor: '#eaeaea', // Grey background color
    color: '#333', // Darker text color for better contrast
    padding: '10px',
    borderRadius: '5px',
    display: 'flex',
    alignItems: 'center'
    // Add more styles here as needed
  };

  const iconStyle = {
    fontSize: '1.5rem', // Adjust the size of the icon as needed
    marginRight: '8px' // Add some space between the icon and the text
  };

  return (
    alert !== null && (
      <div className='grid grid-cols-1 xl:grid-cols-2 lg:grid-cols-2 md:grid-cols-2 mb-8 gap-8'>
        <div style={alertStyle}>
          <i className='fas fa-info-circle' style={iconStyle}></i> {/* Added inline style for the icon */}
          {alert.msg}
        </div>
      </div>
    )
  );
}

export default Alert;
